(58, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Chọn giao diện?', 'Kiểm tra các giao diện tương thích với WooCommerce và chọn một giao diện phù hợp với thương hiệu và nhu cầu kinh doanh của bạn.', '{}', 'unactioned', 'woocommerce-admin', '2025-03-05 02:54:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Cách tùy chỉnh danh mục sản phẩm của bạn', 'Bạn muốn danh mục sản phẩm và hình ảnh của mình trông đẹp mắt và phù hợp với thương hiệu của bạn. Hướng dẫn này sẽ cung cấp cho bạn tất cả các mẹo bạn cần để sản phẩm của bạn trông đẹp mắt trong cửa hàng của bạn.', '{}', 'unactioned', 'woocommerce-admin', '2025-03-05 02:54:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'wc-admin-launch-checklist', 'info', 'en_US', 'Bạn đã sẵn sàng để ra mắt cửa hàng của mình?', 'Để đảm bảo bạn không bao giờ có cảm giác chìm đắm \"mình đã quên điều gì\", chúng tôi đã tập hợp danh sách kiểm tra trước khi ra mắt cần thiết.', '{}', 'unactioned', 'woocommerce-admin', '2025-03-05 02:54:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-mobile-app', 'info', 'en_US', 'Cài đặt ứng dụng di động Woo', 'Cài đặt ứng dụng di động WooCommerce để quản lý đơn đặt hàng, nhận thông báo bán hàng và xem các số liệu chính - mọi lúc mọi nơi.', '{}', 'unactioned', 'woocommerce-admin', '2025-03-05 02:54:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-03-05 02:54:27', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Bảo vệ Của hàng WooCommerce của bạn bằng Jetpack Backup.', 'Thời gian ngừng hoạt động của cửa hàng đồng nghĩa với việc mất doanh thu. Khôi phục bằng một cú nhấp chuột giúp bạn nhanh chóng hoạt động trở lại nếu có sự cố xảy ra.', '{}', 'unactioned', 'woocommerce-admin-notes', '2025-03-06 17:56:01', NULL, 0, 'thumbnail', 'http://threeboy.test/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info'),
(64, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Bắt đầu chấp nhận thanh toán trên cửa hàng của bạn!', 'Nhận thanh toán với nhà cung cấp phù hợp với bạn - chọn từ hơn 100 cổng thanh toán cho WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2025-03-07 16:07:43', NULL, 0, 'plain', '', 0, 0, 'info');
